import React, { useState } from "react";
import characterImg from "./character.png";

// You can keep adding more nodes for new branches, like the snake room!
const storyNodes = {
  start: {
    text: "You enter a maze. Do you want to go left or right?",
    options: [
      { text: "Left", next: "leftBridge", move: "left" },
      { text: "Right", next: "rightSnake", move: "right" },
    ],
  },
  leftBridge: {
    text: "You see a rickety bridge. Do you want to cross it?",
    options: [
      { text: "Yes", next: "bridgeFall", move: "center" },
      { text: "No", next: "hiddenPath", move: "left" },
    ],
  },
  bridgeFall: {
    text: "While you cross, the bridge snaps! You fall. Game over ðŸ’€",
    options: [{ text: "Restart", next: "start", move: "start" }],
  },
  hiddenPath: {
    text: "Smart move. You find a hidden path and a shiny sword. Do you pick it up?",
    options: [
      { text: "Yes", next: "monsterFight", move: "left" },
      { text: "No", next: "monsterLose", move: "left" },
    ],
  },
  monsterFight: {
    text: "A monster appears! Do you swing your sword?",
    options: [
      { text: "Yes", next: "win", move: "center" },
      { text: "No", next: "lose", move: "center" },
    ],
  },
  monsterLose: {
    text: "A monster attacks! You have no weapon. Game over ðŸ˜ˆ",
    options: [{ text: "Restart", next: "start", move: "start" }],
  },
  win: {
    text: "You slay the monster and find a treasure chest. You win! ðŸ†",
    options: [{ text: "Restart", next: "start", move: "start" }],
  },
  lose: {
    text: "The monster attacks and you lose! Game over.",
    options: [{ text: "Restart", next: "start", move: "start" }],
  },
  // Extended path: Right leads to a snake room!
  rightSnake: {
    text: "You walk right and find a dark room filled with snakes. Do you try to run or stay still?",
    options: [
      { text: "Run", next: "escapeSnakes", move: "right" },
      { text: "Stay Still", next: "snakeBite", move: "right" },
    ],
  },
  escapeSnakes: {
    text: "You run fast and barely escape the room, but find yourself at a locked door. Do you try to break it or look for another way?",
    options: [
      { text: "Break Door", next: "doorBreakFail", move: "right" },
      { text: "Look For Another Way", next: "findKey", move: "center" },
    ],
  },
  snakeBite: {
    text: "The snakes surround you... Game over. ðŸ",
    options: [{ text: "Restart", next: "start", move: "start" }],
  },
  doorBreakFail: {
    text: "You try to break the door but set off a trap! Game over.",
    options: [{ text: "Restart", next: "start", move: "start" }],
  },
  findKey: {
    text: "You find a hidden key, unlock the door, and escape the maze! You win! ðŸ—ï¸ðŸŽ‰",
    options: [{ text: "Restart", next: "start", move: "start" }],
  },
};

export default function MazeGame() {
  const [node, setNode] = useState("start");
  const [position, setPosition] = useState("start");

  // Character position logic for animation
  const getPositionClass = () => {
    switch (position) {
      case "left":
        return "character-left";
      case "right":
        return "character-right";
      case "center":
        return "character-center";
      default:
        return "character-start";
    }
  };

  const handleOption = (opt) => {
    setNode(opt.next);
    setPosition(opt.move || position);
  };

  return (
    <div className="game-container">
      <h1>Maze Adventure Game</h1>
      <div className="maze-area">
        <img src={characterImg} alt="Character" className={`character ${getPositionClass()}`} />
      </div>
      <p className="story">{storyNodes[node].text}</p>
      <div className="choices">
        {storyNodes[node].options.map((opt, idx) => (
          <button key={idx} onClick={() => handleOption(opt)}>
            {opt.text}
          </button>
        ))}
      </div>
    </div>
  );
}
